import React from 'react'
import {Link} from 'react-router-dom'
import {connect} from 'react-redux'
import {login} from '../store/actions/authActions'

class Login extends React.Component {
	state = {
		email: '',
		password: '',
		error: {}
	}

	static getDerivedStateFromProps(nextProps, prevState) {
	if (
	JSON.stringify(nextProps.auth.error) !== JSON.stringify(prevState.error)
	) {
	return {
	error: nextProps.auth.error
	};
	}
	return null;
	}

	changeHandler = event => {
		this.setState({
			[event.target.name]: event.target.value
		})
	}

	submitHandler = event => {
		event.preventDefault()
		this.props.login({
			email: this.state.email,
			password: this.state.password
		}, this.props.history)
	}

	render(){
		let { email,password } = this.state;
		return (
			<div className="row">
				<div className="col-md-6 offset-md-3">
					<h1 className="text-center display-4">Login Here</h1>
					<form onSubmit={this.submitHandler}>
						<div className="form-group">
							<label htmlFor="email"> Email: </label>
							<input
								type="text"
								className={
								error.name ? "form-control is-invalid" : "form-control"
								}
								placeholder="Enter Your email"
								name='email'
								id='email'
								value={email}
								onChange={this.changeHandler}
							/>
						</div>
						<div className="form-group">
							<label htmlFor="password"> Password: </label>
							<input
								type="password"
								className="form-control"
								placeholder="Enter Your email"
								name='password'
								id='password'
								value={password}
								onChange={this.changeHandler}
							/>
						</div>
						<Link to='/register'>Dont Have Account? Register Here</Link>
						<button className='btn btn-primary my-3 d-block'>Login</button>
					</form>
				</div>
			</div>
		)
	}
}

const mapStateToProps = state => ({
	auth: state.auth
})

export default connect(mapStateToProps, {login})(Login)
